sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	var iAM = "WIDGET: Notes List";
	// Use a position indicator to know which record to update
	c.notePos = 0;

	c.selectItem = function(position) { 
		/*
		So, when I click in the notes list in th <a class=...>
		the ng-click="c.selectItem($index)" calls this function 
		and passes the $index ($index is merely the position in the list)
		*/
		// Set the position indicator to the index value passed in on select
		c.notePos = position;
		var id = c.data.notes[position].sys_id;
		console.log(iAM + ' the sys_id of the selected note is: ' + id);
		// how do we know what is the selected list item?
		// when we click in the list, we send the idx and it
		// stays this idx until we click again
		console.log(iAM + " we're working on position: [" + position + "] So the position stays: " + c.notePos);
		console.log(iAM + ' Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}
  // Subscribe to event.  Set the new title and note values on the 
	// c.data object

	// RICK: the 'updateTitle' is passed from Notes Body in the
	//       $rootScope.$emit('updateTitle', r.data)
	//       from the Notes Body client script
	$rootScope.$on('updateTitle', function(event,data) { 
		console.log(iAM + " updateTitle event received: " + event);
		console.log(iAM + " " + data);
		// we're back in this widget after updating an item in the notes body widget
		// so we should still be in the same notes position from when we first
		// clicked on the notes list
		console.log(iAM + " We should be working in the same position-which is: " + c.notePos);
		c.data.notes[c.notePos].title = data.title; // data.title passed via data above
		c.data.notes[c.notePos].note = data.note; // data.note passed via data above
	});

}

sp_widget.script:
(function() {
	// RICK: This function loads when the widget loads

	// data.notes will be used in the HTML template
	// where the ng-repeat in the <a class=""...> element
	// cycles through the data.notes object
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};

		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');

		noteObj.note = noteGR.getValue('note').slice(0,20);

		data.notes.push(noteObj);
	}
})();
